
public class Gugudan01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 1;
		while(i<=9){
			int j=1;
			while(j<=9){
				System.out.println(i + " X " + j +" = "+(i*j));
				j= j+1;
			}
			System.out.println("======================");
			i = i + 1;
		}
	}

}
